 RCChartLabelHelper(data = data, textStyle = labelHelperProperties.textStyle)
 Spacer(modifier = Modifier.height(24.dp))
 }
 Row(
 modifier = Modifier
 .fillMaxSize()
 .weight(1f)
 ) {
 val scope = rememberCoroutineScope()
