```kotlin
@Composable
fun ThisWeekTopGoalCard(
 title: String = "이번 주 가장 몰입한 목표는",
 highlight: String = "학습",
 subText: String = "전체 집중 시간의 50%를 차지했어요.",
 infos: List<GoalBarInfo>,
 modifier: Modifier = Modifier
) {
 Box(
 modifier
 .background(
 color = LimberColorStyle.Primary_BG_Normal,
 shape = RoundedCornerShape(16.dp)
 )
 .padding(20.dp)
 ) {
 Column(
 verticalArrangement = Arrangement.spacedBy(18.dp)
 ) {
 // 제목 + 하이라이트
 Text(
 buildAnnotatedString {
 append(title + " ")
 withStyle(SpanStyle(color = LimberColorStyle.Secondary_Main)) {
 append(highlight)
 }
 append("이에요")
 },
 style = LimberTextStyle.Heading4,
 color = LimberColorStyle.Gray900
 )
 // 서브텍스트
 Text(
 text = subText,
 style = LimberTextStyle.Body2,
 color = LimberColorStyle.Gray600
 )

 // 목표+막대 차트 목록
 Column(verticalArrangement = Arrangement.spacedBy(20.dp)) {
 infos.forEach { item ->
 Row(verticalAlignment = Alignment.CenterVertically) {
 Image(
 painter = painterResource(item.iconRes),
 contentDescription = null,
 modifier = Modifier.size(28.dp)
 )
 Spacer(Modifier.width(10.dp))
 Column(
 modifier = Modifier.weight(1f)
 ) {
 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Text(
 text = item.goal,
 style = LimberTextStyle.Body2,
 color = LimberColorStyle.Gray800
 )
 Text(
 text = item.duration,
 style = LimberTextStyle.Body2,
 color = LimberColorStyle.Gray700
 )
 }
 // 막대 Progress
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(7.dp)
 .clip(RoundedCornerShape(4.dp))
 .background(LimberColorStyle.Gray200)
 ) {
 Box(
 modifier = Modifier
 .fillMaxHeight()
 .fillMaxWidth(item.percent)
 .clip(RoundedCornerShape(4.dp))
 .background(item.barColor)
 )
 }
 }
 }
 }
 }
 }
 }
}

// 데이터 모델
data class GoalBarInfo(
 val iconRes: Int,
 val goal: String,
 val duration: String,
 val percent: Float, // 0.0~1.0
 val barColor: Color
)
