@Composable
fun CircularProgressBarWithHandleImage(
 percentage: Float,
 modifier: Modifier = Modifier,
 strokeWidth: Dp = 8.dp,
 backgroundColor: Color = Color.LightGray,
 handleImage: ImageBitmap
) {
 val configuration = LocalConfiguration.current
 val screenWidthDp = configuration.screenWidthDp.dp

 Box(
 modifier = modifier
 .width(screenWidthDp)
 .height(screenWidthDp),
 contentAlignment = Alignment.Center
 ) {
 Canvas(
 modifier = Modifier
 .fillMaxSize()
 .padding(40.dp)
 ) {
 val radius = size.minDimension / 1.5f // 반경을 좀 작게
 val center = Offset(size.width * 0.75f, size.height * 0.25f) // 우측상단 쪽으로 이동

 val gradientBrush = Brush.radialGradient(
 colors = listOf(Color(0xFF2F044B), Color(0xFF8308D2)),
 center = center,
 radius = radius,
 tileMode = TileMode.Clamp
 )

 // 그라디언트로 원 채우기
 drawCircle(
 brush = gradientBrush,
 radius = size.minDimension / 2f,
 center = Offset(size.width / 2f, size.height / 2f)
 )
 }


 Canvas(
 modifier = Modifier
 .fillMaxSize()
 .padding(26.dp)
 ) {
 val sweepAngle = 360 * percentage
 val strokePx = strokeWidth.toPx()

 // 배경 원
 drawArc(
 color = backgroundColor,
 startAngle = -90f,
 sweepAngle = 360f,
 useCenter = false,
 style = Stroke(width = strokePx, cap = StrokeCap.Round)
 )

 // Sweep 그라디언트 Brush
 val sweepGradient = Brush.sweepGradient(
 colors = listOf(Color.White, Color(0xFFB961FF), Color.White),
 center = Offset(size.width / 2, size.height / 2)
 )

 // Arc 그리기
 drawArc(
 brush = sweepGradient,
 startAngle = -90f,
 sweepAngle = sweepAngle,
 useCenter = false,
 style = Stroke(width = strokePx, cap = StrokeCap.Round)
 )

 // 핸들 위치 계산
 val centerX = size.width / 2f
 val centerY = size.height / 2f
 val radius = (size.minDimension - strokePx) / 2f
 val angleInDegrees = (percentage * 360f) - 90f
 val angleInRadians = Math.toRadians(angleInDegrees.toDouble())

