@Composable
fun FocusDegreeBar() {
 var buttonPositions by remember { mutableStateOf(listOf(0f, 0.33f, 0.66f)) }
 var touchX by remember { mutableFloatStateOf(0f) }

 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(Color(0xFF1A1A2E))
 .pointerInput(Unit) {
 detectTapGestures { offset ->
 touchX = offset.x
 val screenWidth = size.width
 val newPositions = buttonPositions.map { pos ->
 val targetX = touchX / screenWidth
 val distance = abs(pos - targetX)
 when {
 distance < 0.15f -> targetX
 pos < targetX -> pos + 0.05f.coerceAtMost(1f - pos)
 else -> pos - 0.05f.coerceAtLeast(0f)
 }
 }
 buttonPositions = newPositions
 }
 }
 ) {
 Row(
 modifier = Modifier
 .align(Alignment.Center)
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 buttonPositions.forEachIndexed { index, pos ->
 Button(
 onClick = {},
 modifier = Modifier
 .weight(1f)
 .padding(8.dp),
 shape = CircleShape
 ) {
 Text(text = index.toString())
 }
 }
 }
 }
}
