 contentAlignment = Alignment.Center
 ) {
 Box(Modifier.padding(20.dp)) {
 WarnModal(onClickModifyButton = {
 coroutineScope.launch {
 sheetState.show()
 }
 })
 }
 }
 }
 RegisterBlockAppBottomSheet(sheetState = sheetState)
 }
