@Preview(showBackground = true)
@Composable
fun ReservationListScreenPreview() {
 ReservationPage()
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun ReservationPage(modifier: Modifier = Modifier) {
 val reservationViewModel: ReservationViewModel = hiltViewModel()
 val uiState by reservationViewModel.uiState.collectAsState()

 val bottomSheetVisible = uiState.isSheetVisible
 val sheetState = rememberModalBottomSheetState(skipPartiallyExpanded = true)
 val isClickedAllSelect = uiState.isClickedAllSelected
 var isLoading by remember { mutableStateOf(false) }
 val snackbarHostState = remember { SnackbarHostState() }
 val scope = rememberCoroutineScope()

 LaunchedEffect(Unit) {
 reservationViewModel.sendEvent(ReservationEvent.OnEnteredScreen)
 reservationViewModel.sideEffect.collect { effect ->
 when (effect) {
 is CommonEffect.IsLoading -> {
 isLoading = effect.isVisible
 }

 is CommonEffect.ShowSnackBar -> {
 scope.launch {
 snackbarHostState.showSnackbar(effect.message)
 }
 }
 }
 }
 }

// if (isLoading) {
// Dialog(onDismissRequest = { /* no-op, 또는 로딩중에는 막기 */ }) {
// LimberAnimation(
// modifier = Modifier
// .size(50.dp),
// resId = R.raw.loading_dark
// )
// }
// }

 Box(Modifier.fillMaxSize()){

 Scaffold(
 containerColor = Gray50,
 contentWindowInsets = WindowInsets(0.dp),
 modifier = Modifier.fillMaxSize(),
 floatingActionButton = {
 ReservationFloatingBtn(onClick = {
 reservationViewModel.sendEvent(ReservationEvent.BottomSheet.Show(true))
 })
 },
 topBar = {
 AnimatedContent(
 targetState = uiState.reservationScreenState,
 transitionSpec = {
 fadeIn(tween(50, easing = FastOutSlowInEasing)) togetherWith
 fadeOut(tween(50, easing = FastOutSlowInEasing))
 }
 ) { state ->
 when (state) {
 ReservationScreenState.Idle -> {
 ReservationTopBar(
 count = uiState.ReservationItemModelList.size,
 onClickModify = {
 reservationViewModel.sendEvent(ReservationEvent.OnClickModifyButton)
 }
 )
 }

 else -> {
 ReservationModifyTopBar(
 isAllCheckButtonChecked = isClickedAllSelect,
 onClickSelectAll = {
 reservationViewModel.sendEvent(ReservationEvent.OnClickAllSelected)
 },
 onClickRemove = {},
 onClickComplete = {
 reservationViewModel.sendEvent(ReservationEvent.OnClickModifyCompleteButton)
 }
 )
 }
 }
 }
 }
 ) { paddingValues ->
 Column(
 modifier = modifier
 .fillMaxSize()
 .padding(paddingValues)
 ) {
 ReservationList(
 modifier = Modifier.padding(horizontal = 20.dp),
 reservationList = uiState.ReservationItemModelList,
 reservationScreenState = uiState.reservationScreenState,
 onToggleChanged = { id, checked ->
 reservationViewModel.sendEvent(ReservationEvent.OnToggleChanged(id, checked))
 },
 onCheckButtonClicked = { id, checked ->
 reservationViewModel.sendEvent(
 ReservationEvent.OnRemoveCheckChanged(
 id,
 checked
 )
 )
 }
 )
 }
 }
 SnackbarHost(snackbarHostState, modifier.align(Alignment.BottomCenter))
 }


 if (bottomSheetVisible) {
 ReservationBottomSheet(
 modifier = Modifier,
 sheetState = sheetState,
 onDismissRequest = {
 reservationViewModel.sendEvent(ReservationEvent.BottomSheet.Close)
 })
 }
}

@Composable
fun ReservationTopBar(
 modifier: Modifier = Modifier,
 count: Int,
 onClickModify: () -> Unit = {}
) {
 Row(
 modifier = modifier
 .fillMaxWidth()
 .padding(start = 20.dp, end = 20.dp, top = 20.dp, bottom = 12.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Row(verticalAlignment = Alignment.CenterVertically) {
 Text(
 text = "진행중인 실험",
 style = LimberTextStyle.Heading4,
 color = Gray800
 )
 Spacer(Modifier.width(4.dp))
 Text(
 text = count.toString(),
 style = LimberTextStyle.Heading4,
 color = Primary_Main
 )
 }
 ModifyButton(onClick = onClickModify)
 }
}

@Composable
fun ReservationModifyTopBar(
 modifier: Modifier = Modifier,
 isAllCheckButtonChecked: Boolean = false,
 onClickSelectAll: (Boolean) -> Unit = {},
 onClickRemove: () -> Unit = {},
 onClickComplete: () -> Unit = {}
) {
 Row(
 modifier = modifier
 .fillMaxWidth()
 .padding(start = 20.dp, end = 20.dp, top = 20.dp, bottom = 12.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 LimberCheckButton(
 isChecked = isAllCheckButtonChecked,
 onClick = onClickSelectAll
 )
 Text(
 text = "전체 선택",
 style = LimberTextStyle.Heading4,
 color = Gray800
 )
 }
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 LimberRoundButton(
 onClick = onClickRemove,
 text = "삭제",
 textColor = Color.White,
 enabled = true,
 containerColor = LimberColorStyle.Gray700
 )
 LimberRoundButton(
 onClick = onClickComplete,
 text = "완료",
 textColor = Gray500,
 enabled = true,
 containerColor = Gray200
 )
 }
 }
}

@Composable
fun ModifyButton(onClick: () -> Unit = {}) {
 Row(
 modifier = Modifier
 .clip(RoundedCornerShape(100.dp))
 .clickable(onClick = onClick)
 .background(Gray300)
 .padding(horizontal = 12.dp, vertical = 10.dp),
 horizontalArrangement = Arrangement.Center,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Image(
 painter = painterResource(R.drawable.ic_write),
 contentDescription = null,
 modifier = Modifier.size(16.dp)
 )
 Spacer(Modifier.width(6.dp))
 Text(
 text = "편집하기",
 style = LimberTextStyle.Body2,
 color = Gray600
 )
 }
}

@Composable
fun ReservationList(
 modifier: Modifier = Modifier,
 reservationList: List<ReservationItemModel>,
 reservationScreenState: ReservationScreenState,
 onToggleChanged: (id: Int, checked: Boolean) -> Unit,
 onCheckButtonClicked: (id: Int, checked: Boolean) -> Unit
) {
 LazyColumn(
 modifier = modifier,
 verticalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 items(reservationList) { item ->
 ReservationItemModelComposable(
 info = item,
 reservationScreenState = reservationScreenState,
 onToggleChanged = { checked ->
 onToggleChanged(item.id, checked)
 },
 onCheckButtonClicked = { checked ->
 onCheckButtonClicked(item.id, checked)
 }
 )
 }
 item {
 Spacer(Modifier.height(20.dp))
 }
 }
}

@Composable
fun ReservationItemModelComposable(
 info: ReservationItemModel,
 reservationScreenState: ReservationScreenState,
 onToggleChanged: (Boolean) -> Unit,
 onCheckButtonClicked: (Boolean) -> Unit = {}
) {
 val backgroundColor = if (info.isToggleChecked) Color.White else Gray100
 val chipTextColor = if (info.isToggleChecked) Primary_Main else Gray500
 val chipBackgroundColor = if (info.isToggleChecked) {
 LimberColorStyle.Primary_Background_Dark
 } else {
 Gray200
 }
 val descriptionColor = if (info.isToggleChecked) Gray600 else Gray400

 val dayText = if (info.reservationInfo.repeatOption != "없음") {
 info.reservationInfo.repeatOption
 } else {
 if (info.reservationInfo.repeatDays.isNotEmpty()) {
 "${info.reservationInfo.repeatDays.joinToString(",")}, "
 } else ""
 }

 Column(
 modifier = Modifier
 .fillMaxWidth()
 .shadow(
 12.dp,
 spotColor = Color(0x14000000),
 shape = RoundedCornerShape(10.dp),
 clip = false
 ) // ✅ 먼저 그림자
 .clip(RoundedCornerShape(10.dp)) // ✅ 나중에 클립
 .background(backgroundColor)
 .padding(20.dp)
 )
 {
 when (reservationScreenState) {
 ReservationScreenState.Idle -> {
 IdleRow(
 info = info,
 chipTextColor = chipTextColor,
 chipBackgroundColor = chipBackgroundColor,
 onToggleChanged = onToggleChanged
 )
 }

 else -> {
 ModifiedRow(
 info = info,
 chipTextColor = chipTextColor,
 chipBackgroundColor = chipBackgroundColor,
 onClickCheckButton = onCheckButtonClicked
 )
 }
 }

 Spacer(Modifier.height(12.dp))
 Text(
 text = info.reservationInfo.title,
 style = LimberTextStyle.Heading3,
 color = if (info.isToggleChecked) Gray800 else Gray600
 )
 Spacer(Modifier.height(6.dp))
 Row {
 Text(
 text = dayText,
 style = LimberTextStyle.Body2,
 color = descriptionColor
 )
 Text(
 text = "${info.reservationInfo.startTime} ~ ${info.reservationInfo.endTime}",
 style = LimberTextStyle.Body2,
 color = descriptionColor
 )
 }
 }
}

@Composable
fun IdleRow(
 info: ReservationItemModel,
 chipTextColor: Color,
 chipBackgroundColor: Color,
 onToggleChanged: (Boolean) -> Unit
) {
 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 LimberFilterChip(
 text = info.reservationInfo.category,
 checked = info.isToggleChecked,
 checkedTextColor = chipTextColor,
 uncheckedTextColor = chipTextColor,
 checkedBackgroundColor = chipBackgroundColor,
 uncheckedBackgroundColor = chipBackgroundColor,
 onCheckedChange = {} // 또는 생략 가능: UI용 디스플레이 Chip일 경우
 )
 LimberToggle(
 checked = info.isToggleChecked,
 onCheckedChange = onToggleChanged,
 modifier = Modifier.size(40.dp, 22.dp)
 )
 }
}

@Composable
fun ModifiedRow(
 info: ReservationItemModel,
 chipTextColor: Color,
 chipBackgroundColor: Color,
 onClickCheckButton: (Boolean) -> Unit
) {
 Row(
 modifier = Modifier.fillMaxWidth(),
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 LimberCheckButton(
 isChecked = info.isRemoveChecked,
 onClick = onClickCheckButton
 )
 LimberFilterChip(
 text = info.reservationInfo.category,
 checked = info.isToggleChecked,
 onCheckedChange = {} // 또는 생략 가능: UI용 디스플레이 Chip일 경우
 )
 }
}

@Composable
fun ReservationFloatingBtn(onClick: () -> Unit) {
 Box(
 modifier = Modifier
 .fillMaxSize()
 .padding(end = 20.dp, bottom = 20.dp),
 contentAlignment = Alignment.BottomEnd
 ) {
 Box(
 modifier = Modifier
 .size(56.dp)
 .shadow(12.dp, CircleShape, clip = false) // clip=false!
 .clip(CircleShape)
 .then(gradientModifier())
 .clickable(onClick = onClick),
 contentAlignment = Alignment.Center
 ) {
 Icon(
 imageVector = Icons.Default.Add,
 contentDescription = null,
 tint = Color.White,
 modifier = Modifier.size(24.dp)
 )
 }

 }
}

