import com.kkh.multimodule.core.ui.ui.component.LimberGradientButton
import kotlinx.coroutines.delay
@Composable
fun AlarmPermissionScreen(navigateToManageApp: () -> Unit) {
 val context = LocalContext.current
 val activity = context as ComponentActivity
 val alarmManager = context.getSystemService(Context.ALARM_SERVICE) as AlarmManager

 var isRequestingPermission by remember { mutableStateOf(false) }
 var hasAlarmPermission by remember {
 mutableStateOf(
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.S) {
 alarmManager.canScheduleExactAlarms()
 } else {
 true
 }
 )
 }
 var hasNotificationPermission by remember { mutableStateOf(true) }

 // POST_NOTIFICATIONS 권한 런타임 요청
 val requestPermissionLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.RequestPermission(),
 onResult = { granted ->
 hasNotificationPermission = granted
 }
 )

 // 앱이 설정 화면에서 돌아왔을 때 권한 재확인
 LifecycleEventEffect(event = Lifecycle.Event.ON_RESUME) {
 if (isRequestingPermission) {
 isRequestingPermission = false
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.S) {
 hasAlarmPermission = alarmManager.canScheduleExactAlarms()
 }
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
 hasNotificationPermission = ContextCompat.checkSelfPermission(
 context,
 Manifest.permission.POST_NOTIFICATIONS
 ) == PackageManager.PERMISSION_GRANTED
 }
 }
 }

 // permission이 모두 있으면 다음페이지 이동
 LaunchedEffect(hasAlarmPermission, hasNotificationPermission) {
 if (hasAlarmPermission && hasNotificationPermission) {
 navigateToManageApp()
 }
 }

 Box(
 Modifier
 .fillMaxSize()
 ) {
 Column(
 Modifier
 .fillMaxSize()
 .padding(horizontal = 20.dp), horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Spacer(Modifier.height(84.dp))
 LimberProgressBar(0.6f)
 Spacer(Modifier.height(40.dp))
 Text(
 "알림을 수신받으려면\n알림 허용이 필요해요",
 textAlign = TextAlign.Center,
 style = LimberTextStyle.Heading3,
 color = Gray800
 )
 Spacer(Modifier.height(16.dp))
 Text(
 "권한에 동의해야 림버를 제대로 사용할 수 있어요",
 style = LimberTextStyle.Body2,
 color = Gray600
 )
 Spacer(Modifier.height(40.dp))
 PermissionBox(
 headText = "알림 허용",
 bodyText = "앱 차단 허용",
 imgResId = R.drawable.ic_permission_alert
 )
 Spacer(Modifier.weight(1f))
 Box(Modifier.fillMaxWidth()) {
 LimberGradientButton(
 modifier = Modifier.fillMaxWidth(),
 onClick = {
 if (!hasAlarmPermission) {
 openExactAlarmSettings(context)
 isRequestingPermission = true
 }
 },
 text = "동의하기"
 )
 }
 Spacer(Modifier.height(20.dp))
 }
 }

 // Show animation if playing
 if (animationPlaying) {
 Dialog(onDismissRequest = { /* no-op */ }) {
 LimberAnimation(
 modifier = Modifier.size(50.dp),
 resId = R.raw.loading_dark
 )
 }
 }
}


