 )
 IconButton(
 onClick = { onClose() },
 modifier = Modifier
 ) {
 Icon(
 imageVector = Icons.Default.Close,
 contentDescription = null,
 tint = Gray600
 )
 }

 }
 Spacer(Modifier.height(33.dp))
 AppList(appInfoList = appInfoList, onClickModifyButton = {})
 Spacer(Modifier.height(24.dp))

 }
 }
}
