@Composable
fun AppList(
 modifier: Modifier = Modifier,
 appInfoList: List<AppInfo>,
 onClickModifyButton: () -> Unit,
 onClickEmptyBox: () -> Unit = {}
) {
 if (appInfoList.isEmpty()) {
 EmptyAppInfoBox(onClickEmptyBox)
 } else {
