@Composable
fun RoundedCircularProgressBar(
 progress: Float, // 0.0 ~ 1.0
 modifier: Modifier = Modifier,
 size: Dp = 280.dp,
 strokeWidth: Dp = 12.dp,
 backgroundColor: Color = Color.LightGray,
 progressColor: Color = Color(0xFF3DDC84),
 animated: Boolean = true
) {
