@Composable
fun HomeScreen(
 onClickButtonTonNavigate: () -> Unit
) {
 val homeViewModel: HomeViewModel = hiltViewModel()
 val uiState by homeViewModel.uiState.collectAsState()
 val context = LocalContext.current
 val appInfoList = uiState.usageAppInfoList

 val appDataList = listOf(
 AppInfo("카카오톡", "com.kakao.talk", null, "6시간 30분"),
 AppInfo("인스타그램", "com.instagram.android", null, "3시간 20분"),
 AppInfo("유튜브", "com.google.android.youtube", null, "5시간"),
 AppInfo("슬랙", "com.slack", null, "1시간")
 )

 var isSheetVisible by remember { mutableStateOf(false) }
 val sheetState = rememberModalBottomSheetState(skipPartiallyExpanded = true)

 LaunchedEffect(Unit) {
 homeViewModel.sendEvent(HomeEvent.EnterHomeScreen(context))
 }

 Column(
 Modifier
 .fillMaxSize()
 .systemBarsPadding()
 .background(Color.Transparent)
 ) {
 Spacer(Modifier.height(12.dp))
 HomeTopBar(
 modifier = Modifier.padding(horizontal = 20.dp),
 onClickRegisterApp = { isSheetVisible = true }
 )

 HomeScreenContent(
 modifier = Modifier.weight(1f),
 appInfoList = appInfoList
 )
 }

 if (isSheetVisible) {
 RegisterBlockAppBottomSheet(sheetState = sheetState, onDismissRequest = {
 isSheetVisible = false
 }, onClickComplete = { checkedAppList ->

 }, appList = appDataList)
 }
}

